import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';

const HomePage = () => {
  const token = localStorage.getItem('authToken');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const images = [
    '../Images/5.jpg',
    '../Images/g.jpg',
    '../Images/3.jpg',
    '../Images/1.jpg',
    '../Images/h.jpg',
    '../Images/g.jpg',
  ];

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 6000);

    return () => clearInterval(intervalId);
  }, [images.length]);

  if (!token) {
    return <Navigate to="/" />;
  }

  const pageStyle = {
    fontFamily: 'Arial, sans-serif',
    color: '#333',
    lineHeight: '1.6',
    textAlign: 'center',
  };

  const headerStyle = {
    backgroundImage: `url(${images[currentImageIndex]})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    color: 'white',
    padding: '100px 20px',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)',
    transition: 'background-image 1s ease-in-out',
  };

  const sectionStyle = {
    padding: '10px 20px',
    textAlign: 'left',
    marginLeft: '10%',
    marginRight: '10%',
  };

  const buttonStyle = {
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  };

  return (
    <div style={pageStyle}>
      <header style={headerStyle}>
        <h1>Welcome to Green World</h1>
        <p>Join us in making the planet greener and cleaner for future generations.</p>
        <button style={buttonStyle}>Get Started</button>
      </header>

      <footer style={{ backgroundColor: '#333', color: 'white', padding: '20px 0', textAlign: 'center' }}>
        <p>&copy; Upcoming Universe. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
