import React, { useState } from 'react';
import Navbar from '../Components/Navbar';

const ShopPage = () => {
  const [cart, setCart] = useState([]);

  const products = [
    {
      id: 1,
      name: 'Eco-Friendly Water Bottle',
      image: '../Images/product1.jpg', // Update with actual image paths
      price: 12.99,
    },
    {
      id: 2,
      name: 'Biodegradable Shopping Bag',
      image: '../Images/product2.jpg',
      price: 5.49,
    },
    {
      id: 3,
      name: 'Sustainable Bamboo Toothbrush',
      image: '../Images/product3.jpg',
      price: 3.99,
    },
    {
      id: 4,
      name: 'Recycled Paper Notebooks',
      image: '../Images/product4.jpg',
      price: 8.99,
    },
  ];

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter((item) => item.id !== productId));
  };

  const pageStyle = {
    fontFamily: 'Arial, sans-serif',
    color: '#333',
    lineHeight: '1.6',
    textAlign: 'center',
  };

  const headerStyle = {
    backgroundImage: 'url("../Images/1.jpg")', // Add the background image path here
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    color: 'white',
    padding: '80px 20px',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)',
  };

  const sectionStyle = {
    padding: '50px 20px',
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '20px',
    maxWidth: '1200px',
    margin: '0 auto',
  };

  const productCardStyle = {
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '20px',
    textAlign: 'center',
    backgroundColor: '#fff',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
  };

  const imageStyle = {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
    borderRadius: '8px',
  };

  const buttonStyle = {
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    marginTop: '10px',
  };

  const cartButtonStyle = {
    position: 'fixed',
    bottom: '20px',
    right: '20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    padding: '15px 25px',
    border: 'none',
    borderRadius: '50px',
    fontSize: '18px',
    cursor: 'pointer',
  };

  return (
    <div>
      <div style={pageStyle}>
        <header style={headerStyle}>
          <h1>Eco-Friendly Products</h1>
          <p>Shop sustainable, eco-friendly products to reduce your carbon footprint.</p>
        </header>

        <section style={sectionStyle}>
          {products.map((product) => (
            <div key={product.id} style={productCardStyle}>
              <img src={product.image} alt={product.name} style={imageStyle} />
              <h3>{product.name}</h3>
              <p>${product.price.toFixed(2)}</p>
              <button
                style={buttonStyle}
                onClick={() => addToCart(product)}
              >
                Add to Cart
              </button>
            </div>
          ))}
        </section>

        <button
          style={cartButtonStyle}
          onClick={() => alert('Proceed to Checkout!')}
        >
          Cart ({cart.length})
        </button>
      </div>

      <footer style={{ backgroundColor: '#333', color: 'white', padding: '20px 0', textAlign: 'center' }}>
        <p>&copy; Upcoming Universe. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default ShopPage;
