import React, { useEffect, useState } from 'react';
import Navbar from '../Components/Navbar';

const HomePage = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const images = [
    '/Images/5.jpg',
    '/Images/g.jpg',
    '/Images/3.jpg',
    '/Images/1.jpg',
    '/Images/h.jpg',
    '/Images/g.jpg',
  ];

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 6000);

    return () => clearInterval(intervalId);
  }, [images.length]);

  return (
    <>
      <style>
        {`
          .page-container {
            font-family: 'Poppins', sans-serif;
            color: #333;
            line-height: 1.6;
            margin: 0;
            padding: 0;
            box-sizing: border-box;
          }

          .header-section {
            background-size: cover;
            background-position: center;
            color: white;
            text-align: center;
            height: 100vh;
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            text-shadow: 2px 2px 6px rgba(0, 0, 0, 0.7);
            transition: background-image 1s ease-in-out;
          }

          .header-content {
            max-width: 800px;
            margin: 0 auto;
          }

          .primary-button {
            padding: 12px 24px;
            background-color: #4caf50;
            color: white;
            border: none;
            border-radius: 5px;
            font-size: 18px;
            cursor: pointer;
            transition: transform 0.3s, box-shadow 0.3s;
          }

          .primary-button:hover {
            transform: scale(1.05);
            box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.2);
          }

          .about-section,
          .initiatives-section {
            padding: 60px 20px;
            max-width: 1200px;
            margin: 0 auto;
            text-align: left;
          }

          .initiatives-section {
            background-color: #f9f9f9;
          }

          .initiatives-section ul {
            list-style-type: disc;
            margin-left: 20px;
          }

          .footer-section {
            background-color: #333;
            color: white;
            text-align: center;
            padding: 20px 0;
            margin-top: 20px;
          }
        `}
      </style>
      <div className="page-container">
        <Navbar />
        <header
          className="header-section"
          style={{
            backgroundImage: `url(${images[currentImageIndex]})`,
          }}
        >
          <div className="header-content">
            <h1>Welcome to Green World</h1>
            <p>Join us in making the planet greener and cleaner for future generations.</p>
            <button className="primary-button">Get Started</button>
          </div>
        </header>

        <section className="about-section">
          <h2>Upcoming Universe</h2>
          <p>
            The resources of Earth belong to all its inhabitants. They are meant to be shared
            and not exploited by humans. Sharing land with animals and plants is our duty, and
            interfering in their lives should be considered a punishable crime. We are merely
            passengers on this planet; Earth does not belong to us. It is our responsibility to
            preserve it for future generations. As the common saying goes:{" "}
            <em>"We do not inherit the Earth from our ancestors; we borrow it from our children."</em>
          </p>
          <p>
            Animals are just as important to our ecosystem as any other element. They help maintain
            the ecological pyramid and are a vital part of our environment. However, we are now
            causing environmental changes that harm animals and plant species. We claim more and more
            land for our homes and cities, leaving less space for them.
          </p>
        </section>

        <section className="initiatives-section">
          <h2>Our Initiatives</h2>
          <ul>
            <li>Tree Plantation Drives</li>
            <li>Waste Management Workshops</li>
            <li>Community Clean-Up Events</li>
            <li>Educational Campaigns</li>
          </ul>
        </section>

        <footer className="footer-section">
          <p>&copy; Upcoming Universe. All rights reserved.</p>
        </footer>
      </div>
    </>
  );
};

export default HomePage;
