import React from 'react';
import Slider from 'react-slick';
import Navbar from '../Components/Navbar';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const BlogsPage = () => {
  const images = [
    '../Images/1.jpg',
    '../Images/2.jpg',
    '../Images/3.jpg',
    '../Images/4.jpg',
  ];

  const pageStyle = {
    fontFamily: 'Arial, sans-serif',
    color: '#333',
    lineHeight: '1.6',
    textAlign: 'center',
  };

  const headerStyle = {
    textAlign: 'center',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)',
    padding: '40px 20px',
    color: 'white',
    position: 'relative',
  };

  const sliderStyle = {
    height: '300px',
    overflow: 'hidden',
  };

  const sliderImageStyle = {
    width: '100%',
    height: '300px',
    objectFit: 'cover',
  };

  const blogListStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '20px',
    marginTop: '20px',
  };

  const blogCardStyle = {
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '20px',
    backgroundColor: '#fff',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s',
  };

  const buttonStyle = {
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    textAlign: 'center',
    textDecoration: 'none',
  };

  const blogData = [
    {
      title: 'The Importance of Sustainability',
      excerpt: 'Sustainability is more than just a buzzword; it is a fundamental shift in how we interact with our planet...',
      link: '/blog/the-importance-of-sustainability',
    },
    {
      title: 'Reducing Waste in Our Daily Lives',
      excerpt: 'In this post, we explore practical tips for reducing waste in your everyday activities, from recycling to reusing...',
      link: '/blog/reducing-waste-in-our-daily-lives',
    },
    {
      title: 'How to Start a Green Initiative in Your Community',
      excerpt: 'If you are passionate about the environment and want to inspire others, hereâ€™s how you can start a green initiative...',
      link: '/blog/how-to-start-a-green-initiative-in-your-community',
    },
    {
      title: 'The Future of Clean Energy',
      excerpt: 'Clean energy is the future. We dive into the technologies shaping the renewable energy landscape...',
      link: '/blog/the-future-of-clean-energy',
    },
  ];

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <div style={pageStyle}>
      <Navbar />
      <header style={headerStyle}>
        <div style={sliderStyle}>
          <Slider {...sliderSettings}>
            {images.map((image, index) => (
              <div key={index}>
                <img src={image} alt={`Slide ${index + 1}`} style={sliderImageStyle} />
              </div>
            ))}
          </Slider>
        </div>
        <h1>Our Blogs</h1>
        <p>Read insightful articles about sustainability, environment, and green practices.</p>
      </header>

      <section>
        <h2>Latest Blog Posts</h2>
        <div style={blogListStyle}>
          {blogData.map((blog, index) => (
            <div
              key={index}
              style={blogCardStyle}
              onMouseEnter={(e) => (e.currentTarget.style.transform = 'scale(1.05)')}
              onMouseLeave={(e) => (e.currentTarget.style.transform = 'scale(1)')}
            >
              <h3>{blog.title}</h3>
              <p>{blog.excerpt}</p>
              <a href={blog.link} style={buttonStyle}>Read More</a>
            </div>
          ))}
        </div>
      </section>

      <footer style={{ backgroundColor: '#333', color: 'white', padding: '20px 0', textAlign: 'center' }}>
        <p>&copy; 2024 Green World. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default BlogsPage;
