import React, { useEffect, useState } from 'react';
import Navbar from '../Components/Navbar';

const HomePage = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const images = [
    '../Images/5.jpg',
    '../Images/g.jpg',
    '../Images/3.jpg',
    '../Images/1.jpg',
    '../Images/h.jpg',
    '../Images/g.jpg',
  ];

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 6000);

    return () => clearInterval(intervalId);
  }, [images.length]);

  const pageStyle = {
    fontFamily: 'Arial, sans-serif',
    color: '#333',
    lineHeight: '1.6',
    textAlign: 'center',
  };

  const headerStyle = {
    backgroundImage: `url(${images[currentImageIndex]})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    color: 'white',
    padding: '100px 20px',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)',
    transition: 'background-image 1s ease-in-out', // Smooth transition between images
  };

  const sectionStyle = {
    padding: '50px 20px',
    textAlign: 'left',
  };

  const buttonStyle = {
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  };

  return (
    <div style={pageStyle}>
      <header style={headerStyle}>
        <h1>Welcome to Nature's Haven</h1>
        <p>Be a part of the journey to nurture and protect our planet for generations to come.</p>
        <button style={buttonStyle}>Join Us</button>
      </header>

      <section style={sectionStyle}>
        <h2>Our Vision</h2>
        <p>
          Nature's Haven is committed to fostering an environmentally conscious society. 
          We aim to empower individuals to create a sustainable world through collective action.
        </p>
      </section>

      <section style={{ ...sectionStyle, backgroundColor: '#f4f4f4' }}>
        <h2>What We Do</h2>
        <ul>
          <li>Reforestation Initiatives</li>
          <li>Comprehensive Recycling Programs</li>
          <li>Eco-Friendly Community Projects</li>
          <li>Awareness and Advocacy Campaigns</li>
        </ul>
      </section>

      <footer style={{ backgroundColor: '#333', color: 'white', padding: '20px 0', textAlign: 'center' }}>
        <p>&copy; Nature's Haven. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
