import React from 'react';
import Navbar from '../Components/Navbar';

const AboutPage = () => {
  const pageStyle = {
    fontFamily: 'Arial, sans-serif',
    color: '#333',
    lineHeight: '1.6',
    textAlign: 'center',
  };

  const sectionStyle = {
    padding: '50px 20px',
    textAlign: 'left',
    maxWidth: '1200px',
    margin: '0 auto',
  };

  const headerStyle = {
    backgroundColor: '#4CAF50',
    color: 'white',
    padding: '80px 20px',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)',
  };

  const buttonStyle = {
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    textAlign: 'center',
  };

  return (
    <div style={pageStyle}>
      <header style={headerStyle}>
        <h1>About Us</h1>
        <p>Learn more about our mission and values.</p>
      </header>

      <section style={sectionStyle}>
        <h2>Our Mission</h2>
        <p>
          We are committed to promoting sustainability, environmental awareness, and green practices that contribute to the preservation of our planet. Our team works relentlessly to inspire others to make a positive impact on the environment and future generations.
        </p>
      </section>

      <section style={{ ...sectionStyle, backgroundColor: '#f4f4f4' }}>
        <h2>Our Values</h2>
        <ul>
          <li><strong>Environmental Sustainability</strong> - We prioritize solutions that promote long-term ecological health.</li>
          <li><strong>Community Engagement</strong> - We believe in the power of collaboration and work with local communities.</li>
          <li><strong>Innovation</strong> - We embrace new technologies and creative ideas for environmental protection.</li>
          <li><strong>Integrity</strong> - We are transparent in our efforts and maintain the highest ethical standards.</li>
        </ul>
      </section>

      <section style={sectionStyle}>
        <h2>Our Team</h2>
        <p>
          Our team consists of passionate individuals dedicated to making a difference. From scientists to community organizers, our collective effort is geared toward a sustainable future.
        </p>
        <button style={buttonStyle}>Join Us</button>
      </section>

      <footer style={{ backgroundColor: '#333', color: 'white', padding: '20px 0', textAlign: 'center' }}>
        <p>&copy; 2024 Green World. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default AboutPage;
