import React from 'react';

const ShopPage = () => {
  const products = [
    {
      id: 1,
      name: 'Eco-Friendly Water Bottle',
      image: '/Images/product1.jpg', // Use a root-relative path
      price: 12.99,
    },
    {
      id: 2,
      name: 'Biodegradable Shopping Bag',
      image: '/Images/product2.jpg',
      price: 5.49,
    },
    {
      id: 3,
      name: 'Sustainable Bamboo Toothbrush',
      image: '/Images/product3.jpg',
      price: 3.99,
    },
    {
      id: 4,
      name: 'Recycled Paper Notebooks',
      image: '/Images/product4.jpg',
      price: 8.99,
    },
  ];  

  const addToCart = (product) => {
    console.log('Added to cart:', product.name);
  };

  const pageStyle = {
    fontFamily: 'Arial, sans-serif',
    color: '#333',
    lineHeight: '1.6',
    textAlign: 'center',
    minHeight: '100vh', // Ensures the page takes at least the full height of the viewport
    display: 'flex',
    flexDirection: 'column',
  };

  const headerStyle = {
    backgroundImage: 'url("../Images/c.jpg")', // Add the background image path here
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    color: 'white',
    padding: '80px 20px',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)',
  };

  const sectionStyle = {
    padding: '50px 20px',
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '20px',
    maxWidth: '1200px',
    margin: '0 auto',
    flex: '1', // This makes the main content grow to take up available space
  };

  const productCardStyle = {
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '20px',
    textAlign: 'center',
    backgroundColor: '#fff',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
  };

  const imageStyle = {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
    borderRadius: '8px',
  };

  const buttonStyle = {
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    marginTop: '10px',
  };

  const footerStyle = {
    backgroundColor: '#333',
    color: 'white',
    padding: '20px 0',
    textAlign: 'center',
    marginTop: 'auto', // Ensures footer stays at the bottom of the page
  };

  return (
    <div style={pageStyle}>
      <div style={headerStyle}>
        <h1>Eco-Friendly Products</h1>
        <p>Shop sustainable, eco-friendly products to reduce your carbon footprint.</p>
      </div>

      <section style={sectionStyle}>
        {products.map((product) => (
          <div key={product.id} style={productCardStyle}>
            <img src={product.image} alt={product.name} style={imageStyle} />
            <h3>{product.name}</h3>
            <p>${product.price.toFixed(2)}</p>
            <button
              style={buttonStyle}
              onClick={() => addToCart(product)}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </section>

      <footer style={footerStyle}>
        <p>&copy; Upcoming Universe. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default ShopPage;
